'use strict';

module.exports = function(sequelize, DataTypes) {
  var Intents = sequelize.define('Intents', {
    intent_id:DataTypes.STRING,
    title: DataTypes.STRING,
    hero: DataTypes.STRING,
    hero_square: DataTypes.STRING,
    hero_gallery: DataTypes.STRING,
    bubble1: DataTypes.TEXT,
    bubble2:DataTypes.TEXT
  }, {
    classMethods: {
      associate: function(models) {
        // associations can be defined here
        Intents.belongsToMany(models.BaseAttributes, {
          through: 'IntentsAttributes'
        });
        Intents.belongsToMany(models.Varietals, {
          through: 'IntentVarietals'
        });

        
      }
    }
  });
  return Intents;
};
