'use strict';
module.exports = function(sequelize, DataTypes) {
  var Wines = sequelize.define('Wines', {
    name: DataTypes.STRING,
    display_name: {
      type: DataTypes.STRING,
      allowNull: false
    },
    description: DataTypes.STRING,
    synonyms: {
      type: DataTypes.STRING
    },
    keywords: {
      type: DataTypes.STRING
    },
    hero: DataTypes.STRING,
    hero_square: DataTypes.STRING,
    hero_gallery: DataTypes.STRING,
    price: {
      type: DataTypes.DECIMAL,
      defaultValue:0.0
    },
    vintage: DataTypes.STRING,
    producer: DataTypes.STRING,
    hero: DataTypes.STRING,
    url: DataTypes.STRING,
    sparkling: {
      type: DataTypes.BOOLEAN,
      defaultValue: false
    },
    dessert: {
      type: DataTypes.BOOLEAN,
      defaultValue: false
    },
    fortified: {
      type: DataTypes.BOOLEAN,
      defaultValue: false
    },
    natural: {
      type: DataTypes.BOOLEAN,
      defaultValue: false
    },
    alcohol: {
      type: DataTypes.DECIMAL,
      defaultValue: 0.0
    },
    type:{
      type: DataTypes.ENUM,
      values: ['white', 'red', 'rose']
    },
    favorite: DataTypes.BOOLEAN
  }, {
    classMethods: {
      associate: function(models) {
        // associations can be defined here

        Wines.belongsToMany(models.Locations, {
          through: 'WinesLocations'
        });
        Wines.belongsToMany(models.TastingNotes, {
          through: 'WinesTastingNotes'
        });
        Wines.belongsToMany(models.Varietals, {
          through: 'WinesVarietals'
        });
        Wines.belongsToMany(models.BaseAttributes, {
          through: 'WinesAttributes'
        });
        Wines.belongsToMany(models.Countries, {
          through: 'WineCountries'
        });
        Wines.belongsToMany(models.Foods, {
          through: 'WineFoods'
        });
        Wines.belongsToMany(models.Appellation, {
          through: 'WineAppellations'
        });
        Wines.hasMany(models.WinesMedia, {
          as: 'Media',
          onDelete: 'cascade',
          hooks: true
        });
      }
    }
  });
  return Wines;
};
